import React, { useState } from "react";
import Row from "./Row";
import { SquareType } from "../Types";

function Grid() {
  function getInitGrid(size: number) {
    let initialSquareList: SquareType[][] = [];
    for (let i = 0; i < size; i++) {
      let tempRow: SquareType[] = [];
      for (let j = 0; j < size; j++) {
        tempRow.push({
          id: i * 10 + j + 1,
          xPos: i,
          yPos: j,
          adjacentBombs: 0,
          flipped: false,
          bomb: false,
        });
      }
      initialSquareList.push(tempRow);
    }
    for (let i = 0; i < 9; i++) {
      let randX = Math.floor(Math.random() * 10);
      let randY = Math.floor(Math.random() * 10);
      while (initialSquareList[randX][randY].bomb === true) {
        randX = Math.floor(Math.random() * 10);
        randY = Math.floor(Math.random() * 10);
      }
      initialSquareList[randX][randY].bomb = true;
    }
    for (let i = 0; i < size; i++) {
      for (let j = 0; j < size; j++) {
        for (let k = -1; k <= 1; k++) {
          if (
            i - 1 >= 0 &&
            j + k >= 0 &&
            j + k <= 9 &&
            initialSquareList[i - 1][j + k].bomb === true
          )
            initialSquareList[i][j].adjacentBombs++;
        }
        for (let k = -1; k <= 1; k++) {
          if (
            k !== 0 &&
            j + k >= 0 &&
            j + k <= 9 &&
            initialSquareList[i][j + k].bomb === true
          )
            initialSquareList[i][j].adjacentBombs++;
        }
        for (let k = -1; k <= 1; k++) {
          if (
            i + 1 <= 9 &&
            j + k >= 0 &&
            j + k <= 9 &&
            initialSquareList[i + 1][j + k].bomb === true
          )
            initialSquareList[i][j].adjacentBombs++;
        }
      }
    }
    return initialSquareList;
  }
  function flipAdjacentSquares(id: number, checkedAlreadyList: number[]) {
    console.log(id);
    console.log(checkedAlreadyList);
    console.log("space");
    let tempList = [...squareList];
    for (let i = 0; i < squareList.length; i++)
      for (let j = 0; j < squareList.length; j++)
        if (
          tempList[i][j].id === id &&
          //tempList[i][j].flipped === false &&
          tempList[i][j].bomb === false &&
          tempList[i][j].adjacentBombs === 0 &&
          !checkedAlreadyList.includes(id)
        ) {
          tempList[i][j].flipped = true;
          setSquareList(tempList);
          for (let k = -1; k <= 1; k++) {
            if (i - 1 >= 0 && j + k >= 0 && j + k <= 9)
              flipAdjacentSquares(tempList[i - 1][j + k].id, [
                ...checkedAlreadyList,
                tempList[i][j].id,
              ]);
          }
          for (let k = -1; k <= 1; k++) {
            if (k !== 0 && j + k >= 0 && j + k <= 9)
              flipAdjacentSquares(tempList[i][j + k].id, [
                ...checkedAlreadyList,
                tempList[i][j].id,
              ]);
          }
          for (let k = -1; k <= 1; k++) {
            if (i + 1 <= 9 && j + k >= 0 && j + k <= 9)
              flipAdjacentSquares(tempList[i + 1][j + k].id, [
                ...checkedAlreadyList,
                tempList[i][j].id,
              ]);
          }
        }
  }
  function gridClickUpdate(id: number) {
    let tempList = [...squareList];
    for (let i = 0; i < squareList.length; i++)
      for (let j = 0; j < squareList.length; j++)
        if (tempList[i][j].id === id) tempList[i][j].flipped = true;
    setSquareList(tempList);
    flipAdjacentSquares(id, []);
  }
  const [squareList, setSquareList] = useState(() => getInitGrid(10));
  return (
    <div className="Grid">
      {squareList.map((row, index) => (
        <Row key={index} row={row} updateSquare={gridClickUpdate} />
      ))}
    </div>
  );
}

export default Grid;
