import React from "react";
import Square from "./Square";
import { SquareType } from "../Types";

interface SquareProps {
  row: SquareType[];
  leftClick: (x: number) => void;
  test: boolean;
}

function Row({ row, leftClick, test }: SquareProps) {
  return (
    <span className="Row">
      {row.map((square, index) => (
        <Square
          test={test}
          key={index}
          square={square}
          updateSquare={leftClick}
        />
      ))}
    </span>
  );
}

export default Row;
