import React, { useState } from "react";
import { SquareType } from "../Types";

interface SquareProps {
  square: SquareType;
  updateSquare: (x: number) => void;
}

function Square({ square, updateSquare }: SquareProps) {
  return (
    <div
      onClick={() => updateSquare(square.id)}
      className={
        "Square" + (square.bomb && " Bomb" + (square.flipped && " Flipped"))
      }
    >
      {square.bomb && "b"}
    </div>
  );
}

export default Square;
