import React, { useState } from "react";
import Square from "./Square";
import { SquareType } from "../Types";

interface SquareProps {
  row: SquareType[];
  updateSquare: (x: SquareType[][]) => void;
}

function Row({ row, updateSquare }: SquareProps) {
  return (
    <span className="Row">
      {row.map((square, index) => (
        <Square key={index} square={square} updateSquare={updateSquare} />
      ))}
    </span>
  );
}

export default Row;
